import { CUSTOMER_LOGO, CUSTOMER_REVIEWS } from '../../data/customerReviews';
import CustomerLogo from '../UI/CustomerLogo/CustomerLogo';
import Subtitle from '../UI/Subtitle/Subtitle';
import Carousel from '../UI/Carousel/Carousel'; 
import './CustomerReviewsBlock.scss';
import CustomerFeedbackItem from '../UI/CustomerFeedbackItem/CustomerFeedbackItem';

function CustomerReviewsBlock() {

    return (
      <div className="customerReviewsBlock">
         <div className="customerReviewsBlock__title">
            <Subtitle>
                {'Отзывы клиентов'}
            </Subtitle>    
        </div>
        <div className="customerReviewsBlock__carouselReviews carouselReviews">
            <Carousel slidesToShow={2} slidesToScroll={2}>
                {CUSTOMER_REVIEWS.map(item => <CustomerFeedbackItem key={item.customer} customer={item.customer} review={item.review}/>)}
            </Carousel>    
        </div>
        <div className="customerReviewsBlock__customerLogo">
            {CUSTOMER_LOGO.map(item => <CustomerLogo key={item.alt} alt={item.alt} img={item.img}/>)}
        </div>
      </div>
    );
  }

  export default CustomerReviewsBlock;
